from . import API
from .endpoints import processor, prompt, transcribe

API.path(
  "transcribe",
  transcribe.transcribe_audio,
  "Creates a string from an audio file"
)

API.path(
  "prompt",
  prompt.create_new_prompt_process,
  "Creates a prompt process and returns id for streaming process status"
)

API.path(
  "processor/response",
  processor.response_handler,
  "Handles the responses generated by processor worker clients"
)

API.socket(
  "prompt",
  prompt.PromptProcessesConsumer,
  "Streams prompt process status & data back to the user client"
)

API.socket(
  "processor",
  processor.ProcessorConsumer,
  "Streams prompt to the processor and receives responses"
)
